sys_variable_value.value:
(function execute(inputs, outputs) {
    var amount = [50400, 4600, 7800, 3000, 24540, 240000, 75000];
    var random1 = Math.floor(Math.random() * amount.length);
    outputs.amount = parseFloat(amount[random1]);
    var doctype = ["Standard sales order", "Rush order"];
    var random2 = Math.floor(Math.random() * doctype.length);
    outputs.doctype = doctype[random2];
    var salesorg = ["Germany", "Singapore", "USA"]
    var random3 = Math.floor(Math.random() * salesorg.length);
    outputs.salesorg = salesorg[random3];

  var randnum = Math.floor(Math.random() * 10000);
outputs.docnum = "000001" + randnum;
})(inputs, outputs);

